handle_intersection = {
	('v', 'l') : ('>','s'),
	('>', 'l') : ('^','s'),
	('^', 'l') : ('<','s'),
	('<', 'l') : ('v','s'),
	('v', 's') : ('v','r'),
	('>', 's') : ('>','r'),
	('^', 's') : ('^','r'),
	('<', 's') : ('<','r'),
	('v', 'r') : ('<','l'),
	('>', 'r') : ('v','l'),
	('^', 'r') : ('>','l'),
	('<', 'r') : ('^','l')
}

def move_cart(cart_id):
	direction, (x,y), intersection_state = carts[cart_id]
	if direction == 'v':
		if tracks[x+1][y] == '|':
			carts[cart_id] = [direction, (x+1,y), intersection_state]
		if tracks[x+1][y] == '\\':
			carts[cart_id] = ['>', (x+1,y), intersection_state]
		if tracks[x+1][y] == '/':
			carts[cart_id] = ['<', (x+1,y), intersection_state]
		if tracks[x+1][y] == '+':
			direction, intersection_state = handle_intersection[(direction, intersection_state)]
			carts[cart_id] = [direction, (x+1,y), intersection_state]
	elif direction == '>':
		if tracks[x][y+1] == '-':
			carts[cart_id] = [direction, (x,y+1), intersection_state]
		if tracks[x][y+1] == '\\':
			carts[cart_id] = ['v', (x,y+1), intersection_state]
		if tracks[x][y+1] == '/':
			carts[cart_id] = ['^', (x,y+1), intersection_state]
		if tracks[x][y+1] == '+':
			direction, intersection_state = handle_intersection[(direction, intersection_state)]
			carts[cart_id] = [direction, (x,y+1), intersection_state]
	elif direction == '^':
		if tracks[x-1][y] == '|':
			carts[cart_id] = [direction, (x-1,y), intersection_state]
		if tracks[x-1][y] == '\\':
			carts[cart_id] = ['<', (x-1,y), intersection_state]
		if tracks[x-1][y] == '/':
			carts[cart_id] = ['>', (x-1,y), intersection_state]
		if tracks[x-1][y] == '+':
			direction, intersection_state = handle_intersection[(direction, intersection_state)]
			carts[cart_id] = [direction, (x-1,y), intersection_state]
	elif direction == '<':
		if tracks[x][y-1] == '-':
			carts[cart_id] = [direction, (x,y-1), intersection_state]
		if tracks[x][y-1] == '\\':
			carts[cart_id] = ['^', (x,y-1), intersection_state]
		if tracks[x][y-1] == '/':
			carts[cart_id] = ['v', (x,y-1), intersection_state]
		if tracks[x][y-1] == '+':
			direction, intersection_state = handle_intersection[(direction, intersection_state)]
			carts[cart_id] = [direction, (x,y-1), intersection_state]
	return carts[cart_id][1]


