carts = {}

def initialize():
	for line in data:
		tracks.append(list(line))
	
	cart_id = 0
	for i,row in enumerate(tracks):
		for j,v in enumerate(row):
			if v in '><':
				tracks[i][j] = '-'
				carts[cart_id] = [v, (i,j), 'l']
				cart_id += 1
			if v in 'v^':
				tracks[i][j] = '|'
				carts[cart_id] = [v, (i,j), 'l']
				cart_id += 1

